import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import FormBuilder from '@components/FormBuilder';
import Preview from '@components/Preview'; 
import SendEmailPage from '@components/SendEmail/SendEmailPage';
import Login from '@components/Login';
import Register from '@components/Register';
import 'react-nestable/dist/styles/index.css';

const App = () => {
  const publicURL = process.env.PUBLIC_URL;

  return (
    <Router basename="/Tenant_Ref">
      <Routes>
        <Route exact path="/" element={<FormBuilder />} />
        <Route exact path="/preview" element={<Preview />} />
        <Route exact path="/send-email" element={<SendEmailPage />} />
        <Route exact path="/login" element={<Login />} />
        <Route exact path="/register" element={<Register />} />
      </Routes>

      {/* Example of using process.env.PUBLIC_URL in a Link */}
      <Link to={`${publicURL}/about`}>About</Link>
    </Router>
  );
};

export default App;
